import Head from 'next/head'
import {Bull, EToro, LinkedIn} from '../components/icons';
import { LabelSeries, Sunburst } from 'react-vis';
import { useState } from 'react';

export function Navigation() {
  return (
    <div style={{
      display: "flex",
      alignItems:"center",
      justifyContent: 'space-between',
      color: "white", 
      background: "#008000",
      padding: '1em 0 0 2em',
      minWidth: "300px",
    }}>
      <div 
        className="navigation"
        style={{        
        display: "flex",
        alignItems:"center"}}>
        <div style={{display:"inline-block", background:"#008000", width:"7em"}}>
          <img src="/leafs-inverted.svg" style={{width:"7em", background:'#fff'}}/>
        </div>
        <div style={{fontSize:'1em', marginTop:"-1em", marginLeft:"-2.0em"}}>
          <h1 style={{lineHeight:"0.85em"}}> Midori Capital</h1>
          <div style={{paddingLeft:"3em"}}>
            Transparent / Social / Profitable 
          </div>
        </div>
        <div className="links" style={{fontSize: "2em", alignSelf: 'flex-end', marginLeft: "2em"}}>
          <div style={{display:'inline-block', padding: '10px 40px', background:'green', color:'white', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>About</div>
          <div style={{display:'inline-block', padding: '10px 40px', background:'white', color:'green', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>Portfolio</div>
          <div style={{display:'inline-block', padding: '10px 40px', background:'green', color:'white', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>Blog</div>        
        </div>
      </div>
      <div style={{display:'inline-flex', fontSize: '3em', marginRight:'0.5em', justifyContent:"space-evenly", alignSelf:'flex-end', justifySelf:"flex-end" }}>
          <a href="https://linkedin.com"><LinkedIn style={{height: '1em'}} className="social" /> </a>
          <a href="https://etoro.com"><EToro style={{height:'1em'}} className="social" /> </a>
      </div>
    </div>    
  )
}

export function Splash() {
  return (
    <div style={{
      background:"white",
      display:'flex',
      width: '100%',
      color: "green",
      fontWeight: 900,
      fontSize: '3em',
      overflow:'hidden',
      paddingRight: 0

    }}>
      <div style={{display:'inline-flex', flex: '1', flexWrap:'wrap', justifyContent: 'space-between', width: '100%', padding: '50px', paddingLeft: '100px'}}> 
        <div style={{minWidth: '500px', maxWidth: '800px', flexGrow: 1}}>
          <h1> Social Trading Excellence </h1>
            <p>
              We are a small low-frequency trading office which values transparency and information flow.
            </p>
        </div>
        <div style={{overflow: 'hidden', flexGrow:1, width: '500px', maxHeight: '700px',  marginLeft: '50px' }} >
          <img src="/stock-logos.jpeg" />
        </div>
      </div>
    </div>    
  )
}

export function HowWeInvest() {
  return (
    <div className="scewedbanner" style={{
      position:'relative',
      background: 'black',
      color: 'white'
    }}>
      <div style={{ fontSize: '1.5em'}}> 
        <h1> How We Invest </h1>          
        <div style={{background:'#eee', borderRadius: '5px', display:'flex'}}>
          <div style={{width: '25%', alignSelf: 'flex-end'}} className="sibling">
            <Bull/>
          </div>
          <div style={{padding: '1em', color: 'black'}} className="sibling">
            <h1> Transparent Trading on 
              <a href="https://etoro.com" className="social">
                <img src="/eToro.svg" alt="EToro" style={{verticalAlign:"bottom", height:'1.5em' }}/>
              </a>
            </h1>
            <div>
              We are not a Black-Box <b>Hedge Fund</b> <br/> 
              We have no Secrets and trade on <b>Etoro</b> publicly. <br/>
              Track and Copy our Trades in RealTime.
            </div>
          </div>
        </div>
      </div>          
    </div>    
  );
}


export function InvestmentStrategySummary() {

  const [selected, setSelected] = useState({});


  const getSelcted = (node) => {
    if (node && node.title) {
      return getSelcted(node.parent).add(node.title)
    }
    return new Set([]);
  }


  function LightenDarkenColor(col, amt) {
    var usePound = false;
    if ( col[0] == "#" ) {
        col = col.slice(1);
        usePound = true;
    }

    var num = parseInt(col,16);

    var r = (num >> 16) + amt;

    if ( r > 255 ) r = 255;
    else if  (r < 0) r = 0;

    var b = ((num >> 8) & 0x00FF) + amt;

    if ( b > 255 ) b = 255;
    else if  (b < 0) b = 0;

    var g = (num & 0x0000FF) + amt;

    if ( g > 255 ) g = 255;
    else if  ( g < 0 ) g = 0;

    return (usePound?"#":"") + (g | (b << 8) | (r << 16)).toString(16);
  }

