import { LabelSeries, Sunburst } from 'react-vis';
import { useEffect, useState } from 'react';

const tree = {
    "title": "",
    "color": "#000000",
    "children": [
      {
        "title": "Short Term",
        "color": '#234F1E',
        "sizeLabel": '20%',
        "children": [
          {"title": "Commodities", "color": "#FF00FF", "sizeLabel": '10%', children: [
            {"title": "Gold", "color": "#FFD700", "sizeLabel": '5%', size: 10},
            {"title": "Natural Gas", "color": "#ffa500", "sizeLabel": '5%', size: 10},
          ]},
          {"title": "Indexes", "color": "#008000", "size": 10, "sizeLabel": '5%' },
          {"title": "Earning Reports", "color": "#3DED97", "size": 10, "sizeLabel": '5%' },
        ]
      }, {
        "title": "Long Term",
        "color": '#00f3f7',
        "sizeLabel": '80%',
        "children": [
          {
            "title": "Other", "color": "#1231A0", sizeLabel: '20%',
              children: [
                {"title": "Finance", "color": "#427aa8", "size": 10, "sizeLabel": '5%'},
                {"title": "Travel", "color": "#05599e", "size": 10, "sizeLabel": '5%' },      
              ]
            },
          {"title": "ETFs", "color": "#84bff0", "size": 40, "sizeLabel": '20%'},
          {"title": "REITs", "color": "#3895D3", "size": 40, "sizeLabel": '20%'},
          {"title": "Technology", "color": "#58CCED", "size": 20, "sizeLabel": '20%'},
        ]
      },
    ]
  }

  function flipTree(tree = {}, path=[]) {
    const nextPath = path.concat(tree.title);
    return (
      (tree.children||[])
        .map( x => flipTree(x, nextPath))
        .reduce( (a, b) => ({...a, ...b}), 
          tree.title? {
            [tree.title]: {
              ...tree,
              path: new Set(nextPath)
            }
          }: {})
    );
  }

  const flippedTree = flipTree(tree);
  const groups = Object.keys(flippedTree);

  export function Strategy() {

    const [isHovering, setHovering] = useState(false);
    const [selected, setSelected] = useState('Finance');

    const colorShade = (col, amt) => {
      col = col.replace(/^#/, '')
      if (col.length === 3) col = col[0] + col[0] + col[1] + col[1] + col[2] + col[2]

      let [r, g, b] = col.match(/.{2}/g);
      ([r, g, b] = [parseInt(r, 16) + amt, parseInt(g, 16) + amt, parseInt(b, 16) + amt])

      r = Math.max(Math.min(255, r), 0).toString(16)
      g = Math.max(Math.min(255, g), 0).toString(16)
      b = Math.max(Math.min(255, b), 0).toString(16)

      const rr = (r.length < 2 ? '0' : '') + r
      const gg = (g.length < 2 ? '0' : '') + g
      const bb = (b.length < 2 ? '0' : '') + b

      return `#${rr}${gg}${bb}`
    }

    const withHighlight = (nodesToHighlight, tree) => { 
      return {
        ...tree,
        color: nodesToHighlight.has(tree.title) ? colorShade(tree.color, -50): tree.color,
        children: (tree.children||[]).map(x=>withHighlight(nodesToHighlight, x))
      }
    }

    const data = withHighlight(
      flippedTree[selected || 'Long Term'].path,
      tree
    );

    const labelStyle = {
      fontSize: '20px',
      textAnchor: 'middle'
    };

    useEffect(()=>{
      if (isHovering) { return; }
      const next = groups[(groups.indexOf(selected)+1) % groups.length];
      const handle = setTimeout(()=>setSelected(next), 2000);
      return ()=>window.clearTimeout(handle);
    }, [isHovering, selected]);

    const Keyword = ({word, text}) => (
      <b
        style={{
          borderBottom: (selected == word) ? `2px solid ${flippedTree[word].color}`: 'none',
          color: flippedTree[word].color, 
          margin: "5px"
        }}
        onMouseLeave={() => setHovering(false) } 
        onMouseEnter={() => {setHovering(true); setSelected(word); }}
      >{
        text
      }</b>
    )


    return (
      <div>
        <div style={{display:'flex', justifyContent: 'center', background:'white', padding: '5em', transform:"rotate(2deg)", width: '120%', position:'relative', left:'-5%', top: '-5%'}}>
          <div style={{transform:"rotate(-2deg)", width: '80%', color: 'black'}}>
            <h1>Investment Strategy</h1>
            <div style={{display:'flex', justifyContent: 'stretch', alignContent: 'stretch', alignItems: 'center', flexWrap: 'wrap', flex: 1}}>
              <Sunburst
                hideRootNode
                colorType="literal"
                data={data}
                width={350}
                height={350}
                onValueMouseOut={() => {
                  setHovering(false);
                }}
                onValueMouseOver={
                  node => {
                    setSelected(node.title)
                    setHovering(true);
                  }
                }>
                <LabelSeries
                  data={[{ 
                      x: 0, 
                      y: 0, 
                      label: selected, 
                      style: labelStyle}, 
                    { x: 0,
                      y: -22,
                      label: flippedTree[selected].sizeLabel }
                  ]}
                />
              </Sunburst>

              <div style={{display:'flex', flexDirection:'column', justifyContent: 'stretch', alignContent: 'stretch', flex:1 }}>
                <div style={{display:'block', borderRadius: '5px', background: '#eee', border:`1px solid ${flippedTree['Long Term'].color}`, padding: '1em', margin: '1em', height: '50%', minWidth: '300px'}}>
                  <h2>80% <Keyword word="Long Term" text="Long Term"/></h2>
                  <div>
                    We believe in the general uptrend of the economy and hence invest in a variety of 
                    <Keyword word="ETFs" text="ETFs,"/>
                    <Keyword word="REITs" text="REITs,"/>
                    <Keyword word="Technology" text="Technology"/> and
                    <Keyword word="Finance" text="Finance"/> stock to benefit from growth and dividends.
                  </div>
                </div>
                <div style={{display:'block', borderRadius: '5px', background: '#eee', border:`1px solid ${flippedTree['Short Term'].color}`, padding: '1em', margin: '1em', height: '50%', minWidth: '300px'}}>
                  <h2>20% <Keyword word="Short Term" text="Short Term"/></h2>
                  <div>
                    Catastrophes, Politics, and <Keyword word="Earning Reports" text="Earning Reports"/> have huge impact on the volatility of the market.
                    We keep track of a selection Companies, <Keyword word="Indexes" text="Indexes"/> and <Keyword word="Commodities" text="Commodities"/> like <Keyword word="Gold" text="Gold"/> and <Keyword word="Natural Gas" text="Natural Gas"/>
                    to invest in unique opportunities.
                    We do this either directly or indirectly by copy trading domain-experts - a unique feature of <a href="hhttps://etoro.tw/3i821Yj" style={{textDecoration: 'none'}}>eToro</a>.
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
