export default function DefaultLayout({ children, frontMatter }) {
  return (
      <>
        <style jsx global>{`
          html,
          body {
            height: 100%;
            width: 100%;
            background: #008000;
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          * {
            box-sizing: border-box;
          }
        `}</style>
      <div style={{
        color: "white", 
        background: "#008000",
        padding: '1em 0 0 2em',
        minWidth: "300px",
        display: "flex",
        alignItems:"center"
      }}>
        <div style={{display:"inline-block", background:"#008000", width:"7em"}}>
          <img src="/leafs-inverted.svg" style={{width:"7em", background:'#fff'}}/>
        </div>
        <div style={{fontSize:'1em', marginTop:"-1em", marginLeft:"-2.0em"}}>
          <h1 style={{lineHeight:"0.85em"}}> Midori Capital</h1>
          <div style={{paddingLeft:"3em"}}>
            Transparent / Social / Profitable 
          </div>
        </div>
        <div style={{fontSize: "3em", alignSelf: 'flex-end', marginLeft: "2em"}}>
          <div style={{display:'inline-block', background:'white', color:'green'}}>Portfolio</div>
        </div>
      </div>
      <div style={{background:"url(/stock-logos.jpeg)", minHeight:'40em'}}>

      </div>

    </>
  )
}
