import Head from 'next/head'
import {Bull, EToro, LinkedIn} from '../components/icons';
import { PieChart } from 'react-minimal-pie-chart';

export function Navigation() {
  return (
    <div style={{
      display: "flex",
      alignItems:"center",
      justifyContent: 'space-between',
      color: "white", 
      background: "#008000",
      padding: '1em 0 0 2em',
      minWidth: "300px",
    }}>
      <div 
        className="navigation"
        style={{        
        display: "flex",
        alignItems:"center"}}>
        <div style={{display:"inline-block", background:"#008000", width:"7em"}}>
          <img src="/leafs-inverted.svg" style={{width:"7em", background:'#fff'}}/>
        </div>
        <div style={{fontSize:'1em', marginTop:"-1em", marginLeft:"-2.0em"}}>
          <h1 style={{lineHeight:"0.85em"}}> Midori Capital</h1>
          <div style={{paddingLeft:"3em"}}>
            Transparent / Social / Profitable 
          </div>
        </div>
        <div className="links" style={{fontSize: "2em", alignSelf: 'flex-end', marginLeft: "2em"}}>
          <div style={{display:'inline-block', padding: '10px 40px', background:'green', color:'white', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>About</div>
          <div style={{display:'inline-block', padding: '10px 40px', background:'white', color:'green', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>Portfolio</div>
          <div style={{display:'inline-block', padding: '10px 40px', background:'green', color:'white', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>Blog</div>        
        </div>
      </div>
      <div style={{display:'inline-flex', fontSize: '3em', marginRight:'0.5em', justifyContent:"space-evenly", alignSelf:'flex-end', justifySelf:"flex-end" }}>
          <a href="https://linkedin.com"><LinkedIn style={{height: '1em'}} className="social" /> </a>
          <a href="https://etoro.com"><EToro style={{height:'1em'}} className="social" /> </a>
      </div>
    </div>    
  )
}

export function Splash() {
  return (
    <div style={{
      background:"white",
      display:'flex',
      width: '100%',
      color: "green",
      fontWeight: 900,
      fontSize: '3em',
      overflow:'hidden',
      paddingRight: 0

    }}>
      <div style={{display:'inline-flex', flex: '1', flexWrap:'wrap', justifyContent: 'space-between', width: '100%', padding: '50px', paddingLeft: '100px'}}> 
        <div style={{minWidth: '500px', maxWidth: '800px', flexGrow: 1}}>
          <h1> Social Trading Excellence </h1>
            <p>
              We are a small low-frequency trading office which values transparency and information flow.
