import Head from 'next/head'
import {Bull, EToro, LinkedIn} from '../components/icons';
import { LabelSeries, Sunburst } from 'react-vis';
import { useEffect, useState } from 'react';

export function Navigation() {
  return (
    <div style={{
      display: "flex",
      alignItems:"center",
      justifyContent: 'space-between',
      color: "white", 
      background: "#008000",
      padding: '1em 0 0 2em',
      minWidth: "300px",
    }}>
      <div 
        className="navigation"
        style={{        
        display: "flex",
        alignItems:"center"}}>
        <div style={{display:"inline-block", background:"#008000", width:"7em"}}>
          <img src="/leafs-inverted.svg" style={{width:"7em", background:'#fff'}}/>
        </div>
        <div style={{fontSize:'1em', marginTop:"-1em", marginLeft:"-2.0em"}}>
          <h1 style={{lineHeight:"0.85em"}}> Midori Capital</h1>
          <div style={{paddingLeft:"3em"}}>
            Transparent / Social / Profitable 
          </div>
        </div>
        <div className="links" style={{fontSize: "2em", alignSelf: 'flex-end', marginLeft: "2em"}}>
          <div style={{display:'inline-block', padding: '10px 40px', background:'green', color:'white', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>
            Invest
          </div>
          <div style={{display:'inline-block', padding: '10px 40px', background:'white', color:'green', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>
            <a href="/#Portfolio">Portfolio</a>
          </div>
          <div style={{display:'inline-block', padding: '10px 40px', background:'green', color:'white', borderRadius: '5px 5px 0 0', marginRight:'5px'}}>
            <a href="/#Team">Team</a>
          </div>    
        </div>
      </div>
      <div style={{display:'inline-flex', fontSize: '3em', marginRight:'0.5em', justifyContent:"space-evenly", alignSelf:'flex-end', justifySelf:"flex-end" }}>
          <a href="https://linkedin.com"><LinkedIn style={{height: '1em'}} className="social" /> </a>
          <a href="https://etoro.com"><EToro style={{height:'1em'}} className="social" /> </a>
      </div>
    </div>    
  )
}

export function Splash() {
  return (
    <div style={{
      background:"white",
      display:'flex',
      width: '100%',
      color: "green",
      fontWeight: 900,
      fontSize: '3em',
      overflow:'hidden',
      paddingRight: 0

    }}>
      <div style={{display:'inline-flex', flex: '1', flexWrap:'wrap', justifyContent: 'space-between', width: '100%', padding: '50px', paddingLeft: '100px'}}> 
        <div style={{minWidth: '500px', maxWidth: '800px', flexGrow: 1}}>
          <h1> Social Trading Excellence </h1>
            <p>
              We are a small low-frequency trading office which values transparency and information flow.
            </p>
        </div>
        <div style={{overflow: 'hidden', flexGrow:1, width: '500px', maxHeight: '700px',  marginLeft: '50px' }} >
          <img src="/stock-logos.jpeg" />
        </div>
      </div>
    </div>    
  )
}

export function HowWeInvest() {
  return (
    <div className="scewedbanner" style={{
      position:'relative',
      background: 'black',
      color: 'white'
    }}>
      <div style={{fontSize: '1.5em'}}> 
        <h1> How We Invest </h1>          
        <div style={{background:'#eee', borderRadius: '5px', display:'flex'}}>
          <div style={{width: '25%', alignSelf: 'flex-end'}} className="sibling">
            <Bull/>
          </div>
          <div style={{padding: '1em', color: 'black'}} className="sibling">
            <h1> Transparent Trading on 
              <a href="https://etoro.com" className="social">
                <img src="/eToro.svg" alt="EToro" style={{verticalAlign:"bottom", height:'1.5em' }}/>
              </a>
            </h1>
            <div>
              We are not a Black-Box <b>Hedge Fund</b> <br/> 
              We have no Secrets and trade on <b>Etoro</b> publicly. <br/>
              Track and Copy our Trades in RealTime.
            </div>
          </div>
        </div>
      </div>          
    </div>    
  );
}

const tree = {
  "title": "",
  "color": "#000000",
  "children": [
    {
      "title": "Short Term",
      "color": '#234F1E',
      "sizeLabel": '20%',
      "children": [
        {"title": "Commodities", "color": "#FF00FF", "sizeLabel": '10%', children: [
          {"title": "Gold", "color": "#FFD700", "sizeLabel": '5%', size: 10},
          {"title": "Natural Gas", "color": "#ffa500", "sizeLabel": '5%', size: 10},
        ]},
        {"title": "Indexes", "color": "#008000", "size": 10, "sizeLabel": '5%' },
        {"title": "Earning Reports", "color": "#3DED97", "size": 10, "sizeLabel": '5%' },
      ]
    }, {
      "title": "Long Term",
      "color": '#00f3f7',
      "sizeLabel": '80%',
      "children": [
        {
          "title": "Other", "color": "#1231A0", sizeLabel: '20%',
            children: [
              {"title": "Finance", "color": "#427aa8", "size": 10, "sizeLabel": '5%'},
              {"title": "Travel", "color": "#05599e", "size": 10, "sizeLabel": '5%' },      
            ]
          },
        {"title": "ETFs", "color": "#84bff0", "size": 40, "sizeLabel": '20%'},
        {"title": "REITs", "color": "#3895D3", "size": 40, "sizeLabel": '20%'},
        {"title": "Technology", "color": "#58CCED", "size": 20, "sizeLabel": '20%'},
      ]
    },
  ]
}

function flipTree(tree = {}, path=[]) {
  const nextPath = path.concat(tree.title);
  return (
    (tree.children||[])
      .map( x => flipTree(x, nextPath))
      .reduce( (a, b) => ({...a, ...b}), 
        tree.title? {
          [tree.title]: {
            ...tree,
            path: new Set(nextPath)
          }
        }: {})
  );
}

const flippedTree = flipTree(tree);
const groups = Object.keys(flippedTree);

export function InvestmentStrategySummary() {

  const [isHovering, setHovering] = useState(false);
  const [selected, setSelected] = useState('Finance');

  const colorShade = (col, amt) => {
    col = col.replace(/^#/, '')
    if (col.length === 3) col = col[0] + col[0] + col[1] + col[1] + col[2] + col[2]

    let [r, g, b] = col.match(/.{2}/g);
    ([r, g, b] = [parseInt(r, 16) + amt, parseInt(g, 16) + amt, parseInt(b, 16) + amt])

    r = Math.max(Math.min(255, r), 0).toString(16)
    g = Math.max(Math.min(255, g), 0).toString(16)
    b = Math.max(Math.min(255, b), 0).toString(16)

    const rr = (r.length < 2 ? '0' : '') + r
    const gg = (g.length < 2 ? '0' : '') + g
    const bb = (b.length < 2 ? '0' : '') + b

    return `#${rr}${gg}${bb}`
  }

  const withHighlight = (nodesToHighlight, tree) => { 
    return {
      ...tree,
      color: nodesToHighlight.has(tree.title) ? colorShade(tree.color, -50): tree.color,
      children: (tree.children||[]).map(x=>withHighlight(nodesToHighlight, x))
    }
  }

  const data = withHighlight(
    flippedTree[selected || 'Long Term'].path,
    tree
  );

  const labelStyle = {
    fontSize: '20px',
    textAnchor: 'middle'
  };

  useEffect(()=>{
    if (isHovering) { return; }
    const next = groups[(groups.indexOf(selected)+1) % groups.length];
    const handle = setTimeout(()=>setSelected(next), 2000);
    return ()=>window.clearTimeout(handle);
  }, [isHovering, selected]);

  const Keyword = ({word, text}) => (
    <b
      style={{
        borderBottom: (selected == word) ? `2px solid ${flippedTree[word].color}`: 'none',
        color: flippedTree[word].color, 
        margin: "5px"
      }}
      onMouseLeave={() => setHovering(false) } 
      onMouseEnter={() => {setHovering(true); setSelected(word); }}
    >{
      text
    }</b>
  )


  return (
    <div>
      <div style={{display:'flex', justifyContent: 'center', background:'white', padding: '5em', transform:"rotate(2deg)", width: '120%', position:'relative', left:'-5%', top: '-5%'}}>
        <div style={{transform:"rotate(-2deg)", width: '80%', color: 'black'}}>
          <h1>Investment Strategy</h1>
          <div style={{display:'flex', justifyContent: 'stretch', alignContent: 'stretch', alignItems: 'center', flexWrap: 'wrap', flex: 1}}>
            <Sunburst
              hideRootNode
              colorType="literal"
              data={data}
              width={350}
              height={350}
              onValueMouseOut={() => {
                setHovering(false);
              }}
              onValueMouseOver={
                node => {
                  setSelected(node.title)
                  setHovering(true);
                }
              }>
              <LabelSeries
                data={[{ 
                    x: 0, 
                    y: 0, 
                    label: selected, 
                    style: labelStyle}, 
                  { x: 0,
                    y: -22,
                    label: flippedTree[selected].sizeLabel }
                ]}
              />
            </Sunburst>

            <div style={{display:'flex', flexDirection:'column', justifyContent: 'stretch', alignContent: 'stretch', flex:1 }}>
              <div style={{display:'block', borderRadius: '5px', background: '#eee', border:`1px solid ${flippedTree['Long Term'].color}`, padding: '1em', margin: '1em', height: '50%', minWidth: '300px'}}>
                <h2>80% <Keyword word="Long Term" text="Long Term"/></h2>
                <div>
                  We believe in the general uptrend of the economy and hence invest in a variety of 
                  <Keyword word="ETFs" text="ETFs,"/>
                  <Keyword word="REITs" text="REITs,"/>
                  <Keyword word="Technology" text="Technology"/> and
                  <Keyword word="Finance" text="Finance"/> stock to benefit from growth and dividends.
                </div>
              </div>
              <div style={{display:'block', borderRadius: '5px', background: '#eee', border:`1px solid ${flippedTree['Short Term'].color}`, padding: '1em', margin: '1em', height: '50%', minWidth: '300px'}}>
                <h2>20% <Keyword word="Short Term" text="Short Term"/></h2>
                <div>
                  Catastrophes, Politics, and <Keyword word="Earning Reports" text="Earning Reports"/> have huge impact on the volatility of the market.
                  We keep track of a selection Companies, <Keyword word="Indexes" text="Indexes"/> and <Keyword word="Commodities" text="Commodities"/> like <Keyword word="Gold" text="Gold"/> and <Keyword word="Natural Gas" text="Natural Gas"/>
                  to invest in unique opportunities.
                  We do this either directly or indirectly by copy trading domain-experts - a unique feature of <a href="https://etoro.com" style={{textDecoration: 'none'}}>eToro</a>.
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export function Team() {
  return (
    <div style={{width: '100%', background: '#24aae2', marginTop:'-5em', padding: '5em', paddingBottom: '20em', display:'flex', justifyContent:'stretch'}}>
      <div style={{width:'80%', display:'flex', flexDirection: 'column', flexWrap: 'wrap', justifyContent: 'stretch', alignContent:'stretch' }}>
        <h1>Executive Team</h1>
        <div style={{display:'flex', justifyContent: 'space-around' , flexDirection: 'row', flexWrap: 'wrap', alignContent: 'flex-start' }}>

          <div style={{borderRadius: '10px', background:'white', padding:'1em', margin:'1em', minWidth: "300px", maxWidth: '500px', alignSelf: 'flex-start'}}>
            <img src="/GaetanoChecinski.png" width="200px"/>
            <h1>Gaetano Checinski</h1>
            <h2>Co-Founder - Mathematician {'&'} Solution Architect</h2>
            <div>
              Gaetano is a software engineer and Entrepreneur with experience in optimization, machine learning and data processing. <br/>
              He's been a technology consultant for several companies like Fraunhofer Research Institute, The Times and Trainline. 
            </div>
          </div>


          <div style={{borderRadius: '10px', background:'white', padding:'1em', margin:'1em', minWidth: "300px", maxWidth: '500px'}}>
            <img src="/abhi.jpeg" width="200px"/>
            <h1>Abhishek Gupta</h1>
            <h2>Co-Founder - Investment Manager {'&'} Technologist</h2>
            <div>
              Abhishek is a serial entrepreneur and expert in cloud infrastructure solutions. <br/>
              He has several years of investment experience and been a technology consultation for companies in the Real Estate business.
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}



export function JoinSubscribe() {
  return (
    <div style={{width: '100%', background: '#008000', color:'white', marginTop:'-5em', padding: '5em', display:'flex', justifyContent:'stretch'}}>
      <div style={{width:'80%', display:'flex', flexDirection: 'column', flexWrap: 'wrap', justifyContent: 'stretch', alignContent:'stretch' }}>
