export default function DefaultLayout({ children, frontMatter }) {
  return (
      <>
        <style jsx global>{`
          html,
          body {
            height: 100%;
            width: 100%;
            background: #fff;
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          * {
            box-sizing: border-box;
          }
        `}</style>
      <div style={{
        color: "white", 
        background: "#008000",
        padding: '1em 0 0 2em',
        minWidth: "300px",
        display: "flex",
        alignItems:"center"
      }}>
        <div style={{display:"inline-block", background:"#008000", width:"7em"}}>
          <img src="/leafs-inverted.svg" style={{width:"7em", background:'#fff'}}/>
        </div>
        <div style={{fontSize:'1em', marginTop:"-1em", marginLeft:"-2.0em"}}>
          <h1 style={{lineHeight:"0.85em"}}> Midori Capital</h1>
          <div style={{paddingLeft:"3em"}}>
            Transparent / Social / Profitable 
          </div>
        </div>
        <div style={{fontSize: "3em", alignSelf: 'flex-end', marginLeft: "2em"}}>
          <div style={{display:'inline-block', padding: '5px', background:'white', color:'green', borderRadius: '5px 5px 0 0'}}>Portfolio</div>
        </div>
      </div>
      <div style={{
        background:"url(/stock-logos.jpeg) green", 
        minHeight:'450px',
        display:'flex',
        width: '100%',
        flexDirection: 'column',
        alignItems: 'center',
        paddingTop: '300px'
      }}>
        <div style={{minWidth: '80%', maxWidth: '900px', background:'green', fontSize:'2em', padding: '1em', }}>
          <h1> Crisis Optimized Portfolio </h1>
